#!/usr/bin/env bash

if [[ -z "$1" ]]; then
    echo "Must provide network name (dev OR ic)" 1>&2
    exit 1
fi

mode=$1
if [ $mode = "dev" ]; then 
    network="local" 
else 
    network=$mode
fi
file_name="./backend/.env.$mode"

source $file_name

dfx deploy --network=$network payment_hub --argument "( \
    record { \
        supported_tokens = vec { \
            record { \
                id = principal \"mxzaz-hqaaa-aaaar-qaada-cai\"; \
                ticker = \"ckBTC\"; \
                xrc_ticker = \"BTC\"; \
                fee = record { val = 10 : nat; decimals = 8 : nat8; };  \
                logo_src = \"data:image/svg+xml;base64,\"; \
            }; \
            record { \
                id = principal \"ss2fx-dyaaa-aaaar-qacoq-cai\"; \
                ticker = \"ckETH\"; \
                xrc_ticker = \"ETH\"; \
                fee = record { val = 2_000_000_000_000 : nat; decimals = 18 : nat8; };  \
                logo_src = \"data:image/svg+xml;base64,\"; \
            }; \
            record { \
                id = principal \"xevnm-gaaaa-aaaar-qafnq-cai\"; \
                ticker = \"ckUSDC\"; \
                xrc_ticker = \"USDC\"; \
                fee = record { val = 10_000 : nat; decimals = 6 : nat8; };  \
                logo_src = \"data:image/svg+xml;base64,\"; \
            }; \
            record { \
